import React, { Component } from 'react'
import '../css/saved.css';
import Button from 'react-bootstrap/Button'
import Card from "react-bootstrap/Card";
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import axios from 'axios';
import Loading from './Loading';

class Saved extends Component {
    constructor() {
        super();
        this.state = {
            dados: [],
            isLoaded: false,
            ultimo: "",
            display1: "block",
            display2: "none"
        };
        this.hancleClick = this.hancleClick.bind(this);
    }
    hancleClick() {
        this.setState({
            display1: "none",
            display2: "block"
        })
    }
    componentDidMount() {
        //const passingThis = this;
        Promise.all([axios('api/newsAll/' + localStorage.getItem('user'))])

            .then(([res1]) => {
                return Promise.all([res1])
                console.log('coitadinhos, não passam de um workshop', res1)
            })
            .then(([res1]) => {
                console.log('FUNCIONAAAAAA:', res1.data.data);
                this.setState({
                    isLoaded: true,
                    dados: res1.data.data
                })
            })
        console.log('ah está bem, lá vem mais uma sériezinha', this.state.dados)
    }

    render() {
        //let lastElement = this.state.dados[this.state.dados - 1];
        console.log('teatrinhos, com falinhas mansas', this.state.dados);
        if (!this.state.isLoaded) {
            return <Loading />
        } else {
            let ultima = "";
            let cor = "";
            let arcozelo = 0;
            return (
                <div>
                <div className="a_SavedBody" style={{display: this.state.display1}}>
                    <h6>Mais recentes</h6>
                    <div>
                        {
                            this.state.dados.reverse().map((lement) => {
                                
                                if (lement.guardado && lement.guardado.length) {
                                    cor = "guardados_vermelho_preenchido.svg"
                                } else {
                                    cor = "guardados_vermelho_contorno.svg"
                                }
                                let src2 = "http://atenas.hopto.org/uploads/" + lement.image;
                                
                                console.log('diz lá', lement.guardado)
                                if (lement.guardado[0]) {
                                    arcozelo++;
                                    if (arcozelo <= 2) {
                                        let ultima = src2;
                                        console.log(src2, 'espinho');
                                        return (
                                            <div className='col-sm-4 a_list_margem' key={lement.id}>
                                                <Card className='a_list_change_card'>
                                                    <a className="linksList" href={lement.url}>
                                                        <Card.Header className='a_list_news '>{lement.topicoNoticia}</Card.Header>
                                                        <Card.Body className='a_list_change_body'>
                                                            <div className="a_divImageNoticia">
                                                                <img className='a_list_radius'
                                                                    src={src2} />
                                                            </div>
                                                            <Card.Title className='a_list_margin_title'>
                                                                <div className='a_list_titulos'>{lement.title}</div>
                                                            </Card.Title>
                                                        </Card.Body>
                                                    </a>
                                                    <div className="btnGuardaList">
                                                        <a type='button' className='a_list_position_news'>
                                                            <img className='a_list_img_guardados'
                                                                src={require(`../images/` + cor)} />
                                                        </a>
                                                    </div>
                                                </Card>
                                            </div>
                                        )
                                    }
                                }
                                console.log('lemos',ultima);    
                            })
                            
                        }
                    </div>
                    <br />
                    <div className="a_saved_collections" onClick={hancleClick()}>
                        <div className="a_saved_title">
                            <div>Coleções</div>
                        </div>
                        <Container>
                            <Row>
                                <Col className="a_saved_collection">
                                    <div className="a_saved_collection_title">ler mais tarde</div>
                                    <img className="a_saved_image" src={ultima} />
                                </Col>
                            </Row>

                        </Container>
                    </div>
                    <br /><br /><br />
                </div>
                <div className="a_SavedBody" style={{display: this.state.display2}}>
                    <h6>Ler mais tarde</h6>
                    <div>
                        {
                            this.state.dados.reverse().map((lement) => {
                                
                                if (lement.guardado && lement.guardado.length) {
                                    cor = "guardados_vermelho_preenchido.svg"
                                } else {
                                    cor = "guardados_vermelho_contorno.svg"
                                }
                                let src2 = "http://atenas.hopto.org/uploads/" + lement.image;
                                
                                console.log('diz lá', lement.guardado)
                                if (lement.guardado[0]) {
                              
                                        let ultima = src2;
                                        console.log(src2, 'espinho');
                                        return (
                                            <div className='col-sm-4 a_list_margem' key={lement.id}>
                                                <Card className='a_list_change_card'>
                                                    <a className="linksList" href={lement.url}>
                                                        <Card.Header className='a_list_news '>{lement.topicoNoticia}</Card.Header>
                                                        <Card.Body className='a_list_change_body'>
                                                            <div className="a_divImageNoticia">
                                                                <img className='a_list_radius'
                                                                    src={src2} />
                                                            </div>
                                                            <Card.Title className='a_list_margin_title'>
                                                                <div className='a_list_titulos'>{lement.title}</div>
                                                            </Card.Title>
                                                        </Card.Body>
                                                    </a>
                                                    <div className="btnGuardaList">
                                                        <a type='button' className='a_list_position_news'>
                                                            <img className='a_list_img_guardados'
                                                                src={require(`../images/` + cor)} />
                                                        </a>
                                                    </div>
                                                </Card>
                                            </div>
                                        )
                                }
                                console.log('lemos',ultima);    
                            })
                            
                        }
                    </div>
                    <br />
                    <br /><br /><br />
                </div>
                </div>
            )
        }
    }
}

export default Saved;
