import React from 'react';
import {secundarynavigation} from '../css/secondary_navigation.css';
import { Redirect } from 'react-router-dom';
import  ReactDOM from 'react-dom';
import axios from 'axios';


class SecondaryNavigation extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            category: [],
            categoria: [],
            isLoaded : false,
            click: 100,
            btn: []
        };

        this.handleClick = this.handleClick.bind(this);
    }


    handleClick (btnid) {
        this.setState({click:btnid});

    }


    componentDidMount() {

        Promise.all([axios('api/categoryUser'), axios('api/category')])

            .then(([res1, res2]) =>{
                return Promise.all([res1, res2])
            })

            .then(([res1, res2]) => {
                this.setState({
                    isLoaded: true,
                    categoria: res2,
                    category: res1,
                });

                console.log('AAAAAAAAAIIIIIII', res1, res2);

            }) ;
    }



    render(){

        let {isLoaded, category, categoria,  click } = this.state;


        if(!isLoaded){
            //console.log('Loading');
            return <div>Loading...</div>;
        }else{
            console.log('category',category, 'categoria',categoria);
            const guarda = [];
            //console.log('AIIIIIIIIIIIII',category);

            category.data.data.map((value) => {
                if (value.user_id == localStorage.getItem('user')){
                    categoria.data.data.map((valua) => {
                        if (value.category_id === valua.id){
                            //console.log('MERDDAAAAAA',valua);
                            guarda.push(valua);
                        }
                    });

                }

            });

            console.log('GUARDAA',guarda);

            return(

                <div className="a_secundarymenu">

                    <button className={this.state.click===100? 'a_card_categorys_active' : 'a_card_categorys'} onClick={() => this.handleClick(100)}>
                        PARA MIM
                        <div hidden={this.state.click !== 100} className="a_category_bolinha"></div>
                    </button>

                    {guarda.map((mauuu) => {

                            return (
                                <button className={this.state.click=== mauuu.id ? 'a_card_categorys_active' : 'a_card_categorys'} key={mauuu.id} id={mauuu.id} onClick={()=>this.handleClick(mauuu.id)}>
                                    {mauuu.category}
                                    <div hidden={this.state.click !== mauuu.id} className="a_category_bolinha"></div>
                                </button>
                            )




                    })}

                </div>

            );
        }


    }

}

export default SecondaryNavigation;
