import React, {Component} from 'react'
import {Redirect} from 'react-router-dom';
import {listnews} from '../css/listnews.css';
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardDeck";
import Button from "react-bootstrap/Button";
import { container, row, col } from "reactstrap";
import SecondaryNavigation from "./SecondaryNavigation";
import axios from 'axios';


class ListNewsList extends Component {

    constructor() {
        super();
        this._isMounted = false;
        this.state = {
            news: [],
            news_topics: [],
            topics: [],
            //news_users: [],
            //users: [],
            //category_users: [],
            isLoaded: false,
            guardados: [],
            click: 100,
            btn: [],
            clickk: 0,
            cor: 0
        };

    }
    handleClick (btnid) {
        this.setState({click:btnid, clickk:btnid});
        console.log(this.state.clickk)
    }
    componentDidMount() {
        this._isMounted = true;
        console.log('Mounted', this._isMounted);
        if (this._isMounted) {
            Promise.all([axios('api/newsCategory/'+localStorage.getItem('user')+'/'+this.state.clickk), axios('api/userCategory/'+localStorage.getItem('user'))])

                .then(([res1, res5]) => {
                    return Promise.all([res1, res5])
                })

                .then(([res1, res5]) => {
                    
                    console.log(res1);
                    this.setState({
                        isLoaded: true,
                        news: res1.data.data,
                        category: res5.data.data
                    })
                });
        }
    }

    componentWillUnmount() {
        console.log('unMounted', this._isMounted);

        this._isMounted = false;
        console.log('unMounted', this._isMounted);

    }


    render(){
        const localuser=localStorage.getItem('user');
        var {isLoaded, news, news_topics, topics, guardados, click} = this.state;

        if (!isLoaded) {
            return <div>Loading...</div>
        } else {

        }
            const show_news=[];
            const rita = "";
            //var i = -1;
            let i = -1;
            
            





        return(
            <div>

                <div className="a_secundarymenu">

                <button className={this.state.click===100? 'a_card_categorys_active' : 'a_card_categorys'} onClick={() => this.handleClick(100)}>
                    PARA MIM
                    <div hidden={this.state.click !== 100} className="a_category_bolinha"></div>
                </button>

                {this.state.category.map((mauuu) => {

                        return (
                            <button className={this.state.click=== mauuu.id ? 'a_card_categorys_active' : 'a_card_categorys'} key={mauuu.id} id={mauuu.id} onClick={()=>this.handleClick(mauuu.id)}>
                                {mauuu.category}
                                <div hidden={this.state.click !== mauuu.id} className="a_category_bolinha"></div>
                            </button>
                        )

                })}

                </div>
                {this.state.news.reverse().map((value) => {
            
            let cor = "";
            if (news.guardado==true) {
                cor = "guardados_vermelho_preenchido.svg"
            } else {
                cor = "guardados_vermelho_contorno.svg"
            }
            return(
                <div className='col-sm-4 a_list_margem'>

                            <Card className='a_list_change_card'>
                                <a className="linksList" href={news.url}>
                                    <Card.Header className='a_list_news '>{news.topico}</Card.Header>
                                    <Card.Body className='a_list_change_body'>
                                        <div className="a_divImageNoticia">
                                            <img className='a_list_radius'
                                                 src={require(`../images/${news.image}`)}/>

                                        </div>
                                        <Card.Title className='a_list_margin_title'>
                                            <div className='a_list_titulos'>{news.title}</div>



                                        </Card.Title>
                                    </Card.Body>
                                </a>



                                <div className="btnGuardaList">
                                    <a type='button' className='a_list_position_news'>
                                        <img className='a_list_img_guardados'
                                             src={require(`../images/`+cor)}/>
                                    </a>
                                </div>


                            </Card>

                        </div>
            )
        
    })}
            </div>


        );
        

    }
}



export default ListNewsList
