import React, { Component, useState, useEffect } from 'react';
import '../css/personalizationgrid.css';

class PersonalizationGridAuthor extends Component {
    constructor(props) {
        super(props);
        console.log(props);
        this.state = {
            selected: [],
            clicked: [],
            // categories: props.categories
            categories: [
                {
                    id: 1,
                    img: "",
                    text: "Sociedade"
                },
                {
                    id: 2,
                    img: "",
                    text: "Moda"
                },
                {
                    id: 3,
                    img: "",
                    text: "Casa"
                },
                {
                    id: 4,
                    img: "",
                    text: "Opini達o"
                },
                {
                    id: 5,
                    img: "",
                    text: "Pessoas"
                },
                {
                    id: 6,
                    img: "",
                    text: "Economia"
                },
                {
                    id: 7,
                    img: "",
                    text: "Cultura"
                },
                {
                    id: 8,
                    img: "",
                    text: "Desporto"
                },
                {
                    id: 9,
                    img: "",
                    text: "Artes"
                }]
        }
        const categories = [
            {
                img: "",
                text: "Sociedade"
            },
            {
                img: "",
                text: "Moda"
            },
            {
                img: "",
                text: "Casa"
            },
            {
                img: "",
                text: "Opini達o"
            },
            {
                img: "",
                text: "Pessoas"
            },
            {
                img: "",
                text: "Economia"
            },
            {
                img: "",
                text: "Cultura"
            },
            {
                img: "",
                text: "Desporto"
            },
            {
                img: "",
                text: "Artes"
            }];
    }

    clickCategory(id) {
        if (this.state.selected.filter(obj => obj == id).length == 0) {
            this.setState((prevState, props) => ({
                selected: prevState.selected.concat(id)
            }))
        } else {
            this.setState((prevState, props) => {
                return { selected: prevState.selected.filter(obj => obj != id) };
            })
        }

    }

    // componentDidMount(){
    //     console.log('BEGIN');
    //     fetch('https://jsonplaceholder.typicode.com/users')
    //     .then(
    //         res => res.json()
    //     )
    //     .then(res =>{
    //         this.setState({
    //             categories : res
    //         })
    //     })
        
    // }

    render() {
        const imgUrl = new URL(this.props.Image);

        return (
            <div className="row carrosselautores">

                {
                    this.state.categories.map((category, index) => {
                        return <div key={index} className="a_personalizationgrid_category" onClick={() => this.clickCategory(category.id)}
                            style={{
                                backgroundImage: this.state.selected.find(obj => obj == category.id) ? `url('https://images.unsplash.com/photo-1541233349642-6e425fe6190e?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&w=1000&q=80')` : 'none',
                                backgroundSize: 'contain',
                                color: this.state.selected.find(obj => obj == category.id) ? 'white' : 'black'
                            }}>{category.text}</div>
                    })
                }
            </div>
        )
    }

}

export default PersonalizationGridAuthor;
