import React, { Component } from 'react'

import Carousel from 'react-bootstrap/Carousel';
import Card from './PersonalizationGrid';

class PersonalizationCarousel extends Component {
    constructor(props) {
        super(props);
    }


    render() {
        return (
            <div>
                <h2>PersonalizationGrid</h2>
                <Carousel>
                    {this.props.dados.map((dado, index) => (
                        <Carousel.Item key={index}>
                            <PersonalizationGrid Image={dado.image} DisplayText={dado.displayText} />
                        </Carousel.Item>
                    ))}
                </Carousel>
            </div >
        )
    }
}

export default PersonalizationCarousel;
