import React from 'react';
import secundarynavigation from '../css/secondary_navigation.css';
import { Redirect } from 'react-router-dom';
import  ReactDOM from 'react-dom';
import axios from 'axios';

class GridSecondaryNavigation extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            categoria: [],
            isLoaded : false,
            click: 100,
            btn: [],
            rita: ""
        };

        this.handleClick = this.handleClick.bind(this);
    }


    handleClick (btnid, idCat) {
        this.setState({click:btnid});
        const id = idCat;
        console.log('meuid',idCat);
        const caminho = "/news/" + id;
        return <Redirect to={caminho} />
    }


    componentDidMount() {

        Promise.all([axios('api/category')])

            .then(([res2]) =>{
                return Promise.all([res2])
