//Registo (imagem, nome, email e password)
import React, {Component} from 'react'
import ReactDOM from 'react-dom'
import Axios from 'axios';
import { Redirect } from 'react-router-dom';
import login from '../css/register.css'

class RegisterView extends Component {
    constructor(props) {
		super(props);
		this.state = {
            file: null,
            email: '',
            username: '',
            password: '',
		};
		this.onFormSubmit = this.onFormSubmit.bind(this);
		this.onChange = this.onChange.bind(this);
		this.onChange2 = this.onChange2.bind(this);
		this.fileUpload = this.fileUpload.bind(this);
	}

	onFormSubmit(e) {
		e.preventDefault();
        this.fileUpload(this.state.file);
        
	}

	onChange(e) {
		this.setState({ 
            file: e.target.files[0],
        });
	}

    onChange2(e) {
		let name = event.target.name;
        let value = event.target.value;
        console.log('handle',name, value);
        let data = {};
        data[name] = value;
        this.setState(data);
	}

	fileUpload(file) {
		let data = new FormData();
		data.append('image', file);
		data.append('email', this.state.email);
		data.append('username', this.state.username);
		data.append('password', this.state.password);
        console.log(this.state.email)
        axios
			.post('/api/register', data, {
				headers: {
					'Content-Type': 'multipart/form-data',
                },
			})
			.then(response => {
				console.log('file upload response', response);
			});
	}
    render() {
        if (this.state.loggedIn) {
            window.location.href = "/main";
        }
        if(localStorage.getItem('user')) {
            return <Redirect to='/main' />;
        }
        return (
            <div id="RegisterPage">

                <form className="FormRegisto" onSubmit={this.onFormSubmit} >
                    <div className="UserRegisto">
                        <img className="ImgUserRegisto" src={require('../images/man-user.svg')}/>
                        <input type="file" id="file" className="inputfile" name="image" value={this.state.image} onChange={this.onChange} />
                        <label htmlFor="file">Choose a file</label>
                    </div>
                    <div className="TodosInput">
                    <div className="DivInput">
                        <img className="IconesInput" src={require('../images/user_total_preenchido .svg')}/>
                        <input className="InputsRegisto" type="text" name="username" value={this.state.username} placeholder="username" onChange={this.onChange2} />
                    </div>

                    <div className="DivInput">
                        <img className="IconesInput" src={require('../images/email.svg')}/>
                        <input className="InputsRegisto" type="text" name="email" value={this.state.email} placeholder="email" onChange={this.onChange2} />
                    </div>

                    <div className="DivInput">
                        <img className="IconesInput" src={require('../images/password.svg')}/>
                        <input className="InputsRegisto" type="password" name="password" value={this.state.password} placeholder="password" onChange={this.onChange2} />
                    </div>

                    <div className="DivInput">
                        <img className="IconesInput" src={require('../images/password.svg')}/>
                        <input className="InputsRegisto" type="password" placeholder="confirmar pass" />
                    </div>
                    </div>
                    <input className="RegistoBtn" type="submit" value="seguinte" />
                </form>
            </div>
        )
    }
}

export default RegisterView;
