import React, {Component} from 'react'
import {Redirect} from 'react-router-dom';
import {listnews} from '../css/listnews.css';
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardDeck";
import Button from "react-bootstrap/Button";
import { container, row, col } from "reactstrap";
import SecondaryNavigation from "./SecondaryNavigation";
import axios from 'axios';


class ListNewsList extends Component {

    constructor() {
        super();
        this._isMounted = false;
        this.state = {
            news: [],
            news_topics: [],
            topics: [],
            //news_users: [],
            //users: [],
            //category_users: [],
            isLoaded: false,
            guardados: [],
            click: 100,
            btn: [],
        };

    }
    handleClick (btnid, ref) {
        this.setState({click:btnid});
        const clickk= '/'+ref;
        console.log('clicou ',clickk);
    }
    componentDidMount() {
        this._isMounted = true;
        console.log('Mounted', this._isMounted);
        if (this._isMounted) {
            Promise.all([axios('api/news'), axios('api/newsTopic'), axios('api/topic'), axios('api/newsUser'), axios('api/userCategory/'+localStorage.getItem('user'))])

                .then(([res1, res2, res3, res4, res5]) => {
                    return Promise.all([res1, res2, res3, res4, res5])
                })

                .then(([res1, res2, res3, res4, res5]) => {
                    console.log('res5', res5);

                    this.setState({
                        isLoaded: true,
                        news: res1,
                        news_topics: res2,
                        topics: res3,
                        guardados: res4,
                        category: res5.data.data
                    })
                });
        }
    }

    componentWillUnmount() {
        console.log('unMounted', this._isMounted);

        this._isMounted = false;
        console.log('unMounted', this._isMounted);

    }


    render(){
        const localuser=localStorage.getItem('user');
        var {isLoaded, news, news_topics, topics, guardados, click} = this.state;

        if (!isLoaded) {
            return <div>Loading...</div>
        } else {

        }
            const show_news=[];
            const rita = "";
            //var i = -1;
            let i = -1;
            {news.data.data.reverse().map((value) => {
                i++;
                show_news.push(value);

                show_news[i].topico = "";
                news_topics.data.data.reverse().map((value2) => {

                    if(value.id == value2.news_id) {
                        topics.data.data.reverse().map((value3) => {
                            if(value2.topic_id == value3.id){
                                show_news[i].topico = value3.topic;
                            }
                        })

                    }
                    
                })
                show_news[i].save=false;
                guardados.data.data.reverse().map((value5)=>{
                    console.log('mano',value.id)
                    if(value.id==value5.news_id && value5.user_id==localuser){
                        show_news[i].save=true;
                    }
                })
            })}
            





        return(
            <div>

                <div className="a_secundarymenu">

                <button className={this.state.click===100? 'a_card_categorys_active' : 'a_card_categorys'} onClick={() => this.handleClick(100)}>
                    PARA MIM
                    <div hidden={this.state.click !== 100} className="a_category_bolinha"></div>
                </button>

                {this.state.category.map((mauuu) => {

                        return (
                            <button className={this.state.click=== mauuu.id ? 'a_card_categorys_active' : 'a_card_categorys'} key={mauuu.id} id={mauuu.id} onClick={()=>this.handleClick(mauuu.id)}>
                                {mauuu.category}
                                <div hidden={this.state.click !== mauuu.id} className="a_category_bolinha"></div>
                            </button>
                        )

                })}

                </div>

            <div className='row a_list_padding'>
                {show_news.map((value4, key) =>{
                    let cor = "";
                    console.log('cor', show_news[key])
                    if (show_news[key].save==true) {
                        cor = "guardados_vermelho_preenchido.svg"
                    } else {
                        cor = "guardados_vermelho_contorno.svg"
                    }
                    return (
                        <div className='col-sm-4 a_list_margem' key={value4.id}>

                            <Card className='a_list_change_card'>
                                <a className="linksList" href={value4.url}>
                                    <Card.Header className='a_list_news '>{value4.topico}</Card.Header>
                                    <Card.Body className='a_list_change_body'>
                                        <div className="a_divImageNoticia">
                                            <img className='a_list_radius'
                                                 src={require(`../images/${value4.image}`)}/>

                                        </div>
                                        <Card.Title className='a_list_margin_title'>
                                            <div className='a_list_titulos'>{value4.title}</div>



                                        </Card.Title>
                                    </Card.Body>
                                </a>



                                <div className="btnGuardaList">
                                    <a type='button' className='a_list_position_news'>
                                        <img className='a_list_img_guardados'
                                             src={require(`../images/`+cor)}/>
                                    </a>
                                </div>


                            </Card>

                        </div>
                    )
                })}
            </div>

            </div>



        );


    }
}



export default ListNewsList
