import React, { Component } from 'react'

import Carousel from 'react-bootstrap/Carousel';
import PersonalizationGridAuthor from './PersonalizationGridAuthor';
import '../css/personalizationcarousel.css';
import axios from 'axios'

class PersonalizationCarouselAuthor extends Component {
    constructor(props) {
        super(props);
        this.state={
            isLoaded: false,
            dados:[]
        }
    }

    componentDidMount() {
        Promise.all([axios('api/author')])

            .then(([res1]) => {
                return Promise.all([res1])
            })
            .then(([res1]) => {
                this.setState({
                    dados: res1.data.data
                })
            })
    }

    render() {
        return (
            <Carousel className="a_personalizationgrid_carousel">
                {this.props.dados.map((dado, index) => (
                    <Carousel.Item key={index} >
                        <PersonalizationGridAuthor categories={dado.categories} />
                    </Carousel.Item>
                ))}
            </Carousel>
        )
    }
}

export default PersonalizationCarouselAuthor;
