import React, { Component } from 'react';
import { Redirect, Link } from 'react-router-dom';
import { HomePage_styles } from '../css/HomePage.css';
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardDeck";
import Button from "react-bootstrap/Button";
import { container, row, col } from "reactstrap";
import axios from 'axios';
import GridSecondaryNavigation from './GridSecondaryNavigation'


class ListNewsGrid extends Component {


    constructor(props) {
        super(props);
        this._isMounted = false;
        this.state = {
            news: [],
            news_topics: [],
            topics: [],
            guardados: [],
            isLoaded: false,
            categoria: [],
            click: 100,
            btn: [],
            showMore: false,
            esconde: false
        };
        this.handleClick = this.handleClick.bind(this);
        this.handleClicka = this.handleClicka.bind(this);
        this.handleClickVerMais = this.handleClickVerMais.bind(this);

    }
    handleClickVerMais() {
        this.setState({ showMore: true, esconde: true });
        document.getElementById("botaoVerMais").style.display = "none";


    }
    handleClicka() {
        console.log('ola');
    }
    handleClick(btnid) {
        this.setState({ click: btnid, clickk: btnid });
        Promise.all([axios('api/newsCategory/' + localStorage.getItem('user') + '/' + btnid)])

            .then(([res1]) => {
                return Promise.all([res1])
            })

            .then(([res1]) => {
                console.log(res1);

                this.setState({
                    isLoaded: true,
                    news: res1.data.data
                })
            });

    }
    componentDidMount() {
        this._isMounted = true;
        if (this._isMounted) {
            Promise.all([axios('api/newsAll/' + localStorage.getItem('user')), axios('api/category')])
                .then(([res1, res5]) => {
                    return Promise.all([res1, res5])
                })
                .then(([res1, res5]) => {
                    this.setState({
                        isLoaded: true,
                        news: res1.data.data,
                        categoria: res5.data.data
                    })
                });

        }
    }

    render() {
        let estados = [];
        var { isLoaded, news, news_topics, topics, guardados, categoria, click } = this.state;
        const localuser = '1';
        // const localuser=localStorage.getItem('user');
        if (!isLoaded) {
            return <div>Loading...</div>
        } else {
            return (
                <div>
                    <div className="a_secundarymenu">
                        <button className={this.state.click === 100 ? 'a_card_categorys_active' : 'a_card_categorys'} onClick={() => this.handleClick(100)}>
                            DESTAQUE
                        <div hidden={this.state.click !== 100} className="a_category_bolinha"></div>
                        </button>
                        {categoria.map((value) => {

                            const idClick = '/' + value.id;
                            return (
                                <button className={this.state.click === value.id ? 'a_card_categorys_active' : 'a_card_categorys'} key={value.id} id={value.id} onClick={() => this.handleClick(value.id, value.id)}>
                                    {value.category}

                                    <div hidden={this.state.click !== value.id} className="a_category_bolinha"></div>
                                    <Link to={idClick} />
                                </button>
                            )
                        })}

                    </div>
                    <div className='row a_homepage_padding'>
                    
                    {   
                        
                        news.map((value4, key) => {
                        let cor = "";
                        if (value4.guardado==true) {
                            cor = "guardados_vermelho_preenchido.svg"
                        } else {
                            cor = "guardados_vermelho_contorno.svg"
                        }
                        if (key == 0) {
                            return (
                                <div className='col-sm-12 a_homepage_destaque ' key={value4.id}>

                                    <Card className='a_homepage_change_card'>
                                        <a className="LinkGrid" href={value4.url}>

                                            <Card.Header className='a_homepage_topicos_destaque, a_homepage_change_header_destaque '>{value4.topicoNoticia}</Card.Header>
                                            <Card.Body className='a_homepage_change_body'>
                                                <Card.Title>
                                                    <div className='a_homepage_titulos_destaque'>{value4.title}</div>
                                                </Card.Title>
                                                <Card.Img className='a_homepage_radius'
                                                          src={require(`../images/${value4.image}`)}/>
                                            </Card.Body>

                                        </a>
                                    </Card>
                                    <a type='button' onClick={()=> {this.handleClicka()}} className='a_homepage_guardados'><img
                                        className='a_homepage_img_guardados'
                                        src={require(`../images/`+cor)}/></a>
                                </div>

                            )

                        } else {
                            
                            return (
                                <div className='col-sm-4 a_homepage_margem' key={value4.id}>

                                    <Card className='a_homepage_change_card'>
                                        
                                            <Card.Header className='a_homepage_news'>{value4.topicoNoticia}</Card.Header>
                                            <Card.Body className='a_homepage_change_body'>
                                            <a href={value4.url}>
                                                <Card.Img className='a_homepage_radius'
                                                          src={require(`../images/${value4.image}`)}/>
                                            </a>
                                            <button onClick={()=> {this.handleClick()}} className='a_homepage_position_news'><img
                                                    className='a_homepage_img_guardados'
                                                    src={require(`../images/`+cor)}/>
                                                    </button>
                                            </Card.Body>
                                            <a href={value4.url}>
                                                <Card.Title className='a_homepage_margin_title'>
                                                    <div className='a_homepage_titulos'>{value4.title}</div>
                                                </Card.Title>
                                            </a>
                                        
                                    </Card>

                                </div>
                            )
                        }


                    })
                    }

<button id='botaoVerMais' className='VerMaisBtn' onClick={()=>this.handleClickVerMais()}>Ver mais</button>
                </div>
                </div>
            )

        }
    }

}


export default ListNewsGrid;
