import React, { Component } from 'react'
import '../css/saved.css';
import Button from 'react-bootstrap/Button'
import Card from "react-bootstrap/Card";
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import axios from 'axios';

class Saved extends Component {
    componentDidMount() {
        Promise.all([axios('api/newsAll/' + localStorage.getItem('user'))])
            .then(([res1]) => {
                return Promise.all([res1])
            })
            .then(([res1]) => {
                this.setState({
                    isLoaded: true,
                    news: res1.data.data
                })
            });
    }

    render() {
        let lastElement = this.state.news[this.state.news.lenght-1];
        
        if (!isLoaded) {
            return <div>Loading...</div>
        } else {
            if (lastElement.guardado && lastElement.guardado.length) {
                cor = "guardados_vermelho_preenchido.svg"
            } else {
                cor = "guardados_vermelho_contorno.svg"
            }
            let src2 = "http://atenas.hopto.org/uploads/" + lastElement.image;
            return (
                <div className="a_SavedBody">
                    <h6>Mais recentes</h6>
                    <div>
                        <div className='col-sm-4 a_list_margem' key={lastElement.id}>

                            <Card className='a_list_change_card'>
                                <a className="linksList" href={lastElement.url}>
                                    <Card.Header className='a_list_news '>{lastElement.topicoNoticia}</Card.Header>
                                    <Card.Body className='a_list_change_body'>
                                        <div className="a_divImageNoticia">

                                            <img className='a_list_radius'
                                                src={src2} />
                                        </div>
                                        <Card.Title className='a_list_margin_title'>
                                            <div className='a_list_titulos'>{lastElement.title}</div>



                                        </Card.Title>
                                    </Card.Body>
                                </a>



                                <div className="btnGuardaList">
                                    <a type='button' className='a_list_position_news'>
                                        <img className='a_list_img_guardados'
                                            src={require(`../images/` + cor)} />
                                    </a>
                                </div>


                            </Card>

                        </div>
                    </div>
                    <div className="a_saved_collections">
                        <div className="a_saved_title">
                            <div>Coleções</div>
                        </div>
                        <Container>
                            <Row>
                                <Col className="a_saved_collection" xs={6}>
                                    <div className="a_saved_collection_title">ler mais tarde</div>
                                    <img className="a_saved_image" src={require('../images/logo_atenas.png')} />
                                </Col>
                            </Row>

                        </Container>
                    </div>
                </div>
            )
        }
    }
}

export default Saved;
