import React, {Component} from 'react';
import {Redirect, Link} from 'react-router-dom';
import {HomePage_styles} from '../css/HomePage.css';
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardDeck";
import Button from "react-bootstrap/Button";
import {container, row, col} from "reactstrap";
import axios from 'axios';
import GridSecondaryNavigation from './GridSecondaryNavigation'


class ListNewsGrid extends Component {


    constructor(props) {
        super(props);
        this._isMounted = false;
        this.state = {
            news: [],
            news_topics: [],
            topics: [],
            guardados: [],
            isLoaded: false,
            categoria: [],
            click: 100,
            btn: [],
            showMore: false,
            esconde: false
        };
        this.handleClick = this.handleClick.bind(this);
        this.handleClicka = this.handleClicka.bind(this);
        this.handleClickVerMais = this.handleClickVerMais.bind(this);

    }
    handleClickVerMais() {
        this.setState({showMore:true, esconde: true});
        document.getElementById("botaoVerMais").style.display = "none";


    }
    handleClicka() {
        console.log('ola');
    }
    handleClick(btnid, ref) {
        this.setState({click:btnid});
        const clickk= '/'+ref;
        console.log('clicou ',clickk);
        const filter = clickk;
    }
    componentDidMount() {
        this._isMounted = true;
        if (this._isMounted) {
            Promise.all([axios('api/news'), axios('api/newsTopic'), axios('api/topic'), axios('api/newsUser'), axios('api/category')])
                .then(([res1, res2, res3, res4, res5]) => {
                    return Promise.all([res1, res2, res3, res4, res5])
                })
                .then(([res1, res2, res3, res4, res5]) => {
                    this.setState({
                        isLoaded: true,
                        news: res1,
                        news_topics: res2,
                        topics: res3,
                        guardados: res4,
                        categoria: res5
                    })
                });

        }
    }

    render() {
        let estados = [];
        var {isLoaded, news, news_topics, topics, guardados, categoria, click} = this.state;
        const localuser = '1';
        // const localuser=localStorage.getItem('user');
        if (!isLoaded) {
            return <div>Loading...</div>
        } else {
            const guarda = [];
            categoria.data.data.map((value) => {
                guarda.push(value);
            });
            const show_news = [];
            //var i = -1;
            let i = -1;
            {
                news.data.data.reverse().map((value) => {
                    i++;
                    show_news.push(value);
                    show_news[i].topico = "";
                    news_topics.data.data.reverse().map((value2) => {

                        if (value.id == value2.news_id) {
                            topics.data.data.reverse().map((value3) => {
                                if (value2.topic_id == value3.id) {
                                    show_news[i].topico = value3.topic;
                                }

                            })
                        }
                    });

                    show_news[i].save=false;
                    guardados.data.data.reverse().map((value5)=>{
                        if(value.id==value5.news_id && value5.user_id==localuser){
                            show_news[i].save=true;
                        }
                    })

                });
            }
            const numberOfItems=this.state.showMore ? show_news.length : 3;
            if(this.state.esconde){
                show_news.reverse();
            }
            return (
                                <div>
                                    <div className="a_secundarymenu">

                
                <button className={this.state.click===100? 'a_card_categorys_active' : 'a_card_categorys'} onClick={() => this.handleClick(100)}>
                    DESTAQUE
                    <div hidden={this.state.click !== 100} className="a_category_bolinha"></div>
                </button>

                {guarda.map((mauuu) => {
                    
                    const idClick = '/'+mauuu.id;
                    return (
                        <button className={this.state.click=== mauuu.id ? 'a_card_categorys_active' : 'a_card_categorys'} key={mauuu.id} id={mauuu.id} onClick={()=>this.handleClick(mauuu.id, mauuu.id)}>
                            {mauuu.category}
                            
                            <div hidden={this.state.click !== mauuu.id} className="a_category_bolinha"></div>
                            <Link to={idClick} />
                        </button>
                    )




                })}

</div>

                <div className='row a_homepage_padding'>
                    
                    {   
                        
                        show_news.slice(0,numberOfItems).map((value4, key) => {
                        let cor = "";
                        if (show_news[key].save==true) {
                            cor = "guardados_branco_preenchido.svg"
                        } else {
                            cor = "guardados_branco_contorno.svg"
                        }
                        if (key == 0) {
                            return (
                                <div className='col-sm-12 a_homepage_destaque ' key={value4.id}>

                                    <Card className='a_homepage_change_card'>
                                        <a className="LinkGrid" href={value4.url}>

                                            <Card.Header className='a_homepage_topicos_destaque, a_homepage_change_header_destaque '>{value4.topico}</Card.Header>
                                            <Card.Body className='a_homepage_change_body'>
                                                <Card.Title>
                                                    <div className='a_homepage_titulos_destaque'>{value4.title}</div>
                                                </Card.Title>
                                                <Card.Img className='a_homepage_radius'
                                                          src={require(`../images/${value4.image}`)}/>
                                            </Card.Body>

                                        </a>
                                    </Card>
                                    <a type='button' onClick={()=> {this.handleClicka()}} className='a_homepage_guardados'><img
                                        className='a_homepage_img_guardados'
                                        src={require(`../images/`+cor)}/></a>
                                </div>

                            )

                        } else {
                            
                            return (
                                <div className='col-sm-4 a_homepage_margem' key={value4.id}>

                                    <Card className='a_homepage_change_card'>
                                        
                                            <Card.Header className='a_homepage_news'>{value4.topico}</Card.Header>
                                            <Card.Body className='a_homepage_change_body'>
                                            <a href={value4.url}>
                                                <Card.Img className='a_homepage_radius'
                                                          src={require(`../images/${value4.image}`)}/>
                                            </a>
                                            <button onClick={()=> {this.handleClick()}} className='a_homepage_position_news'><img
                                                    className='a_homepage_img_guardados'
                                                    src={require(`../images/`+cor)}/>
                                                    </button>
                                            </Card.Body>
                                            <a href={value4.url}>
                                                <Card.Title className='a_homepage_margin_title'>
                                                    <div className='a_homepage_titulos'>{value4.title}</div>
                                                </Card.Title>
                                            </a>
                                        
                                    </Card>

                                </div>
                            )
                        }


                    })
                    }

<button id='botaoVerMais' className='VerMaisBtn' onClick={()=>this.handleClickVerMais()}>Ver mais</button>
                </div>
                </div>
            )
        }
        //}
    }

}


export default ListNewsGrid;
