import React, { Component } from 'react';
import '../css/personalizationgrid.css';

class PersonalizationGrid extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clicked: []
        }
    }
    clickCategory(id) {
        console.log('--> ', id, this.state.clicked);
        this.setState((prevState) => {
            console.log('? ', prevState.clicked.find(obj => obj == id), prevState.clicked.find(clicked => clicked == id));
            if (!prevState.clicked.find(clicked => clicked == id))
                prevState.clicked.push(id);
        }
        )
    }
    render() {
        const imgUrl = new URL(this.props.Image);
        const categories = [
            {
                img: "",
                text: "Sociedade"
            },
            {
                img: "",
                text: "Moda"
            },
            {
                img: "",
                text: "Casa"
            },
            {
                img: "",
                text: "Opinião"
            },
            {
                img: "",
                text: "Pessoas"
            },
            {
                img: "",
                text: "Economia"
            },
            {
                img: "",
                text: "Cultura"
            },
            {
                img: "",
                text: "Desporto"
            },
            {
                img: "",
                text: "Artes"
            }];

        return (
            <div className="row">
                {

                    categories.map((category, index) => {
                        return <div className="a_personalizationgrid_category" onClick={() => this.clickCategory(index)}
                            style={{
                                 backgroundImage: this.state.clicked.find(clicked => clicked == index) ? `url('https://i.pinimg.com/originals/e4/34/2a/e4342a4e0e968344b75cf50cf1936c09.jpg')` : 'none',
                                backgroundSize: 'contain'
                            }}>{category.text}</div>
                    })
                }
            </div>
        )
    }

}

export default PersonalizationGrid;
