import React, {Component} from 'react'
import ReactDOM from 'react-dom';
import { Redirect } from 'react-router-dom';
import axios from 'axios';


class Home extends Component {

    constructor(props) {
        super(props);
        this.state = {
            news: [],
            isLoaded: false    
        };
    }
    componentDidMount() {

        axios.get('/api/news')
            .then(response=>{
                
                let news = response.data.data;
                console.log(news)
                this.setState({
                    isLoaded: true,
                    news: news,
                })
                console.log('aqui senhor',news)
            })

    }
    
    render() {
        var { isLoaded, news } = this.state;
        if (!isLoaded) {
            return <div>Loading...</div>
        }
