import React, { Component } from 'react'

import Carousel from 'react-bootstrap/Carousel';
import PersonalizationGridAuthor from './PersonalizationGridAuthor';
import '../css/personalizationcarousel.css';

class PersonalizationCarouselAuthor extends Component {
    constructor(props) {
        super(props);
    }


    render() {
        return (
            <Carousel className="a_personalizationgrid_carousel">
                {this.props.dados.map((dado, index) => (
                    <Carousel.Item key={index} >
                        <PersonalizationGridAuthor Image={dado.image} categories={dado.categories} />
                    </Carousel.Item>
                ))}
            </Carousel>
        )
    }
}

export default PersonalizationCarouselAuthor;
