import React, { Component } from 'react'

import Carousel from 'react-bootstrap/Carousel';
import PersonalizationGridAuthor from './PersonalizationGridAuthor';
import '../css/personalizationcarousel.css';
import axios from 'axios';

class PersonalizationCarouselAuthor extends Component {
    constructor(props) {
        super(props);
        this.state={
            isLoaded: false,
            dados:[],
            authors:[]
        }
    }

    componentDidMount() {
        const passingThis = this;
        Promise.all([axios('api/author')])

            .then(([res1]) => {
                return Promise.all([res1])
            })
            .then(([res1]) => {
                console.log('FUNCIONAAAAAA:', res1.data.data);
                passingThis.setState({
                    isLoaded: true,
                    dados: res1.data.data
                })
            })
    }

    render() {
        console.log('Autores funciona porra:', this.state.dados);
        return (
            <Carousel className="a_personalizationgrid_carousel">
                {this.state.dados.map((dado, index) => (
                    <Carousel.Item key={index} >
                        <PersonalizationGridAuthor authors={dado.author} />
                    </Carousel.Item>
                ))}
            </Carousel>
        )
    }
}

export default PersonalizationCarouselAuthor;
