import React, { Component, useState } from 'react';
import '../css/personalizationgrid.css';

class PersonalizationGrid extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clicked: []
        }
    }
    clickCategory(id) {
        this.setState((prevState) => {
            if (!prevState.clicked.find(clicked => clicked == id)) {
                return {clicked : prevState.clicked.push(id)};
            }
        }
        )
    }
    // checkClicked(index) {
    //     console.log('--> ', this.state.clicked.find(clicked => clicked == index) != undefined);
    //     return this.state.clicked.find(clicked => clicked == index) != undefined;
    // }
    render() {
        const imgUrl = new URL(this.props.Image);
        const categories = [
            {
                img: "",
                text: "Sociedade"
            },
            {
                img: "",
                text: "Moda"
            },
            {
                img: "",
                text: "Casa"
            },
            {
                img: "",
                text: "Opinião"
            },
            {
                img: "",
                text: "Pessoas"
            },
            {
                img: "",
                text: "Economia"
            },
            {
                img: "",
                text: "Cultura"
            },
            {
                img: "",
                text: "Desporto"
            },
            {
                img: "",
                text: "Artes"
            }];
        return (
            <div className="row">
                <p>{this.state.clicked}</p>
                {

                    categories.map((category, index) => {
                        return <div className="a_personalizationgrid_category" onClick={() => this.clickCategory(index)}
                            style={{
                                backgroundImage: `url('https://i.pinimg.com/originals/e4/34/2a/e4342a4e0e968344b75cf50cf1936c09.jpg')`,
                                backgroundSize: 'contain'
                            }}>{category.text}</div>
                    })
                }
            </div>
        )
    }

}

export default PersonalizationGrid;
