//Registo (imagem, nome, email e password)
import React, {Component} from 'react'
import ReactDOM from 'react-dom'
import axios from 'axios';
import { Redirect } from 'react-router-dom';
import login from '../css/register.css'

class RegisterView extends Component {
    constructor(props) {
		super(props);
		this.state = {
            file: null,
            email: '',
            username: '',
            password: '',
            msgErroEmail: "",
            msgErroUser: "",
            msgErroPass: "",
            bigError: "",
		};
		this.onFormSubmit = this.onFormSubmit.bind(this);
		this.onChange = this.onChange.bind(this);
		this.onChange2 = this.onChange2.bind(this);
		this.fileUpload = this.fileUpload.bind(this);
	}
    handleBlurPass(event) {
        let value = event.target.value;
        if (value.length>=30) {
            this.setState({
                msgErroPass: <div>Password demasiado longa!</div>,
            })
        }
        if (value.length<=8) {
            this.setState({
                msgErroPass: <div>Password demasiado curta!</div>,
            })
            if (value=="") {
                this.setState({
                    msgErroPass: <div>Preencha a password!</div>,
                })
            }
        }
    }
    handleBlurUser(event) {
        let value = event.target.value;
        if (value=="") {
            this.setState({
                msgErroUser: <div>Preencha o nome!</div>,
            })
        }
        if (value.length>=30) {
            this.setState({
                msgErroUser: <div>Nome demasiado longo!</div>,
            })
        }
        let format = /[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/;
        if((format.test(value)) == true) {
            
            this.setState({
                msgErroUser: <div>Nome demasiado longo!</div>,
            })
        }
    }
    handleBlur(event) {
        let value = event.target.value;
        axios.get('/api/users')
            .then(response=>{
                let emails = response.data.data;
                emails.map((mail) => {
                    if (mail.email==value) {
                        this.setState({
                            msgErroEmail: <div>Email já registado!</div>,
                        })
                    }
                })
        })
        let re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        if ( !re.test(value) ) {
            this.setState({
                msgErroEmail: <div>Email inválido!</div>,
            })
            if (value=="") {
                this.setState({
                    msgErroEmail: <div>Preencha o email!</div>,
                })
            } 
        } 
    }
	onFormSubmit(e) {
		e.preventDefault();
        this.fileUpload(this.state.file);
        
	}

	onChange(e) {
		this.setState({ 
            file: e.target.files[0],
        });
	}

    onChange2(event) {
		let name = event.target.name;
        let value = event.target.value;
        console.log('handle',name, value);
        let data = {};
        data[name] = value;
        this.setState(data);
	}

	fileUpload(file) {
		let data = new FormData();
		data.append('image', file);
		data.append('email', this.state.email);
		data.append('username', this.state.username);
		data.append('password', this.state.password);
        console.log(this.state.email)
        axios
			.post('/api/register', data, {
				headers: {
					'Content-Type': 'multipart/form-data',
                },
			})
			.then(response => {
                console.log('file upload response', response);
                localStorage.setItem('user', response.data.user.id);
                return <Redirect to='/main' />;
            })
            .catch(error => {
                console.log('erro',error);
                this.setState({bigError: <div>Registo inválido! Tente novamente.</div>});
            })
            ;
	}
    render() {
        if (this.state.loggedIn) {
            window.location.href = "/main";
        }
        if(localStorage.getItem('user')) {
            return <Redirect to='/main' />;
        }
        return (
            <div id="RegisterPage">

                <form className="FormRegisto" onSubmit={this.onFormSubmit} >
                    <div className="UserRegisto">
                        <img className="ImgUserRegisto" src={require('../images/man-user.svg')}/>
                        <input type="file" id="file" className="inputfile" name="image" value={this.state.image} onChange={this.onChange} />
                        <label htmlFor="file">Choose a file</label>
                    </div>
                    <div className="TodosInput">
                    <div className="DivInput">
                        <img className="IconesInput" src={require('../images/user_total_preenchido .svg')}/>
                        <input className="InputsRegisto" type="text" name="username" value={this.state.username} placeholder="username" onChange={this.onChange2} />
                        {this.state.msgErroUser}
                    </div>

                    <div className="DivInput">
                        <img className="IconesInput" src={require('../images/email.svg')}/>
                        <input className="InputsRegisto" type="text" name="email" value={this.state.email} placeholder="email" onChange={this.onChange2} />
                        {this.state.msgErroEmail}
                    </div>

                    <div className="DivInput">
                        <img className="IconesInput" src={require('../images/password.svg')}/>
                        <input className="InputsRegisto" type="password" name="password" value={this.state.password} placeholder="password" onChange={this.onChange2} />
                        {this.state.msgErroPass}
                    </div>

                    <div className="DivInput">
                        <img className="IconesInput" src={require('../images/password.svg')}/>
                        <input className="InputsRegisto" type="password" placeholder="confirmar pass" />
                    </div>
                    </div>
                    {this.state.bigError}
                    <input className="RegistoBtn" type="submit" value="seguinte" />
                </form>
            </div>
        )
    }
    
}

export default RegisterView;
