import React, {Component} from 'react'
import {Redirect} from 'react-router-dom';
import {listnews} from '../css/listnews.css';
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardDeck";
import Button from "react-bootstrap/Button";
import { container, row, col } from "reactstrap";
import SecondaryNavigation from "./SecondaryNavigation";
import axios from 'axios';


class ListNewsList extends Component {

    constructor() {
        super();
        this._isMounted = false;
        this.state = {
            news: [],
            news_topics: [],
            topics: [],
            //news_users: [],
            //users: [],
            //category_users: [],
            isLoaded: false
        };

    }

    componentDidMount() {
        this._isMounted = true;
        console.log('Mounted', this._isMounted);
        if (this._isMounted) {
            Promise.all([axios('api/news'), axios('api/newsTopic'), axios('api/topic')])

                .then(([res1, res2, res3]) => {
                    return Promise.all([res1, res2, res3])
                })

                .then(([res1, res2, res3]) => {
                    console.log(res1, res2, res3);

                    this.setState({
                        isLoaded: true,
                        news: res1,
                        news_topics: res2,
                        topics: res3,
                        //news_users: res4,
                        //users: res5,
                        //category_users: res6
                    })
                });
        }
    }

    componentWillUnmount() {
        console.log('unMounted', this._isMounted);

        this._isMounted = false;
        console.log('unMounted', this._isMounted);

    }


    render(){

        var {isLoaded, news, news_topics, topics} = this.state;

        if (!isLoaded) {
            return <div>Loading...</div>
        } else {

        }
            const show_news=[];
            //var i = -1;
            let i = -1;
            {news.data.data.reverse().map((value) => {
                i++;
                show_news.push(value);
                show_news[i].topico = "";
                news_topics.data.data.reverse().map((value2) => {

                    if(value.id == value2.news_id) {
                        topics.data.data.reverse().map((value3) => {
                            if(value2.topic_id == value3.id){
                                show_news[i].topico = value3.topic;
                            }
                        })

                    }
                })

            })}





        return(
            <div>

                <SecondaryNavigation />

            <div className='row a_homepage_padding'>
                {show_news.map((value4, key) =>{
                    return (
                        <div className='col-sm-4 a_homepage_margem' key={value4.id}>

                            <Card className='a_homepage_change_card'>
                                <a href={value4.url}>
                                    <Card.Header className='a_homepage_news'>{value4.topico}</Card.Header>
                                    <Card.Body className='a_homepage_change_body'>
                                        <div className="a_divImageNoticia">

                                            <img className='a_homepage_radius'
                                                 src={require(`../images/${value4.image}`)}/>

                                        </div>
                                        <Card.Title className='a_homepage_margin_title'>
                                            <div className='a_homepage_titulos'>{value4.title}</div>



                                        </Card.Title>
                                    </Card.Body>
                                </a>



                                <div className="btnGuardaList">
                                    <a type='button' className='a_homepage_position_news'>
                                        <img className='a_homepage_img_guardados'
                                             src={require(`../images/guardados_vermelho_contorno.svg`)}/>
                                    </a>
                                </div>


                            </Card>

                        </div>
                    )
                })}
            </div>

            </div>



        );


    }
}



export default ListNewsList
