import React, { Component } from 'react';
import '../css/personalizationgrid.css';

class PersonalizationGrid extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clicked: []
        }
    }
    clickCategory(id){
        console.log('--> ', id, this.state.clicked);
        this.setState((prevState) => {prevState.clicked.push() })
    }
    render() {
        const imgUrl = new URL(this.props.Image);
        const categories = [
            {
                img: "",
                text: "Sociedade"
            },
            {
                img: "",
                text: "Moda"
            },
            {
                img: "",
                text: "Casa"
            },
            {
                img: "",
                text: "Opinião"
            },
            {
                img: "",
                text: "Pessoas"
            },
            {
                img: "",
                text: "Economia"
            },
            {
                img: "",
                text: "Cultura"
            },
            {
                img: "",
                text: "Desporto"
            },
            {
                img: "",
                text: "Artes"
            }];
           
        return (
            <div className="row">
                {
                    
                    categories.map((category, index)=>{
                    return <div className="a_personalizationgrid_category" onClick={() => this.clickCategory(index)} 
                    style={{backgroundImage: `url('https://i.pinimg.com/originals/e4/34/2a/e4342a4e0e968344b75cf50cf1936c09.jpg')`,
                        backgroundSize: 'contain'}}>{category.text}</div>
                    })
                }
            </div>
        )
    }

}

export default PersonalizationGrid;
