//Main - inclui as componentes da home, meu jornal e guardados
//Inclui a componente do header e do menu de navegação
import React, { Component } from 'react'
//Exemplo para usar imagem - depois mete-se atenas no src por exmeplo
//import atenas from '../images/logo_atenas.png'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import Home from './HomeView';
import Saved from '../components/Saved';
import MyNewspaper from '../components/MyNewspaper';

import { Redirect } from 'react-router-dom';
import { Nav, Tabs, Tab } from 'react-bootstrap';

export const Logout = () => {
  localStorage.clear();
  window.location.href = "/session";
}

class MainView extends Component {

  render() {
    if (localStorage.getItem('user') == null) {
      return <Redirect to='/session' />;
    }
    return (
      <Router>
        <div>
          {/* <h1>Main</h1>
            <ul>
              <li>
                <Link to="/my">O meu jornal</Link>
              </li>
              <li>
                <Link to="/news">Home </Link>
              </li>
              <li>
                <Link to="/saved">Guardados</Link>
              </li>
              <li onClick={Logout}>
                Logout
              </li>
            </ul>
            <Switch>
                <Route path="/news">
                    <Home />
                </Route>
                <Route path="/my">
                    <MyNewspaper />
                </Route>
                <Route path="/saved">
                    <Saved />
                </Route>
            </Switch> */}



          {/* NAVBAR BOOTSTRAP */}
          {/* <Nav
            fill={true}
            style={{position: "absolute", bottom: 0}}
            activeKey="/home"
            onSelect={selectedKey => alert(`selected ${selectedKey}`)}
          >
            <Nav.Item>
              <Nav.Link href="/home">Active</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="link-1">Link</Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="link-2">Link</Nav.Link>
            </Nav.Item>
          </Nav> */}

          {/* TABS BOOTSTRAP */}

          <Tabs defaultActiveKey="home" transition={false} id="noanim-tab-example" style="position: 'absolute', bottom: 0 ,width:100%">
            <Tab eventKey="home" title="Home">
             1
            </Tab>
            <Tab eventKey="profile" title="Profile">
              2
            </Tab>
            <Tab eventKey="profile2" title="Profile2">
              3
            </Tab>
          </Tabs>
        </div>
      </Router>
    )
  }
}

export default MainView;
